mapper.select:
SELECT i.id,i.softwareName,i.softwareSize, d1.valueName AS flatformName, category1.categoryName AS categoryLevel1Name, category2.categoryName AS categoryLevel2Name, category3.categoryName AS categoryLevel3Name, d2.valueName AS statusName, i.downloads,v.versionNo,i.versionId,i.status FROM app_info i INNER JOIN (SELECT * FROM data_dictionary) d1 ON i.flatformId = d1.valueId INNER JOIN (SELECT * FROM app_category) category1 ON i.categoryLevel1 = category1.id INNER JOIN (SELECT * FROM app_category) category2 ON i.categoryLevel2 = category2.id INNER JOIN (SELECT * FROM app_category) category3 ON i.categoryLevel3 = category3.id INNER JOIN (SELECT * FROM data_dictionary) d2 ON i.status = d2.valueId LEFT JOIN (SELECT * FROM app_version) v ON i.versionId = v.id
d1.typeCode = 'APP_FLATFORM' AND d2.typeCode = 'APP_STATUS'
AND i.softwareName like concat('%',#{softwareName},'%')
AND i.flatformId = #{flatformId}
AND i.categoryLevel1 = #{categoryLevel1}
AND i.categoryLevel2 = #{categoryLevel2}
AND i.categoryLevel3 = #{categoryLevel3}
limit #{fromLineNum},#{toLineNum}

SELECT
    count(*)
FROM
    app_info i


            AND i.softwareName like concat('%',#{softwareName},'%')


            AND i.flatformId = #{flatformId}


            AND i.categoryLevel1 = #{categoryLevel1}


            AND i.categoryLevel2 = #{categoryLevel2}


            AND i.categoryLevel3 = #{categoryLevel3}
